// movie-detail.component.ts

import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ApiService } from '../api.service'; 
import { MovieService } from '../movie.service';

@Component({
  selector: 'app-movie-detail',
  templateUrl: './movie-detail.component.html',
  styleUrls: ['./movie-detail.component.css'],
})
export class MovieDetailComponent implements OnInit {
  movieId: string| null = null;
  movieTitle: string| null = null;
  errorMessage: string | null = null;
  successMessage: string | null = null;

  constructor(
    private route: ActivatedRoute,
    private movieService: MovieService
  ) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      this.movieId = params.get('id');
      // console.log('id',this.movieId)
      if (this.movieId) {
        // Fetch movie details
        // console.log('id',this.movieId)
        this.movieService.getMovieDetails(this.movieId).subscribe(
          (movieDetails: any) => {
            this.movieTitle = movieDetails.title;
          },
          error => {
            this.errorMessage = 'Error fetching movie details. Please check if the movie ID is valid.';
          }
        );
      }
    });
  }

  bookmarkMovie() {
    if (this.movieId) {
      this.movieService.bookmarkMovie(this.movieId).subscribe(
        response => {
          this.successMessage = response.message;
        },
        error => {
          this.errorMessage = 'Error bookmarking the movie. Please try again.';
        }
      );
    } else {
      // Handle the case where this.movieId is null or undefined, if needed
    }
  }
}
